let tbody_content = '<tr>';
EXT_DETAIL.forEach( (item) => {
    tbody_content += '<td>';
    tbody_content += item['id'];
    tbody_content += "</td><td>";
    tbody_content += item["name"];
    tbody_content += "</td><td>";
    tbody_content += item["designation"];
    tbody_content += "</td><td>";
    tbody_content += item["extNo"];
    tbody_content += "</td></tr>";
});
const tbody = document.querySelector("#table-body");
tbody.innerHTML = tbody_content;


const inputbox = document.querySelector('#search_text');
inputbox.addEventListener('keyup', (e) =>{
    e.preventDefault();
    let search_text = inputbox.value.toLowerCase(); 
    let result  = EXT_DETAIL.filter(item => item.name.toLowerCase().includes(search_text) || item.designation.toLowerCase().includes(search_text))
    tbody_content = '<tr>';
    result.forEach((item)=>{
        tbody_content += '<td>';
        tbody_content += item['id'];
        tbody_content += "</td><td>";
        tbody_content += item["name"];
        tbody_content += "</td><td>";
        tbody_content += item["designation"];
        tbody_content += "</td><td>";
        tbody_content += item["extNo"];
        tbody_content += "</td></tr>";
    });
    tbody.innerHTML = tbody_content;
})
